import React, { useState, useEffect, useRef } from 'react';

// Chatbot component
const Chatbot = () => {
    // State to store chat messages
    const [messages, setMessages] = useState([]);
    // State to store the current input message
    const [input, setInput] = useState('');
    // State for loading indicator during API calls
    const [isLoading, setIsLoading] = useState(false);
    // State for loading indicator specifically for insights generation
    const [isGeneratingInsights, setIsGeneratingInsights] = useState(false);
    // Ref to scroll to the bottom of the chat messages
    const messagesEndRef = useRef(null);

    // Scroll to the latest message whenever messages state changes
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    // Function to send message to Gemini API for regular chat
    const sendMessage = async (e) => {
        e.preventDefault(); // Prevent form submission default behavior

        if (input.trim() === '') return; // Don't send empty messages

        const userMessage = { text: input, sender: 'user' };
        setMessages((prevMessages) => [...prevMessages, userMessage]); // Add user message to chat
        setInput(''); // Clear input field
        setIsLoading(true); // Show loading indicator for chat

        try {
            // Prepare chat history for the API call
            let chatHistory = messages.map(msg => ({
                role: msg.sender === 'user' ? 'user' : 'model',
                parts: [{ text: msg.text }]
            }));
            chatHistory.push({ role: "user", parts: [{ text: input }] }); // Add current user input

            const payload = { contents: chatHistory };
            const apiKey = ""; // Leave this as is; Canvas will provide the API key at runtime
            const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`;

            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
            });

            const result = await response.json();

            if (result.candidates && result.candidates.length > 0 &&
                result.candidates[0].content && result.candidates[0].content.parts &&
                result.candidates[0].content.parts.length > 0) {
                const botResponseText = result.candidates[0].content.parts[0].text;
                const botMessage = { text: botResponseText, sender: 'bot' };
                setMessages((prevMessages) => [...prevMessages, botMessage]); // Add bot response
            } else {
                console.error("Unexpected API response structure:", result);
                const errorMessage = { text: "Sorry, I couldn't get a response. Please try again.", sender: 'bot' };
                setMessages((prevMessages) => [...prevMessages, errorMessage]);
            }
        } catch (error) {
            console.error("Error calling Gemini API:", error);
            const errorMessage = { text: "There was an error connecting to the AI. Please check your network or try again later.", sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, errorMessage]);
        } finally {
            setIsLoading(false); // Hide loading indicator
        }
    };

    // Function to generate insights from chat history using Gemini API
    const generateInsights = async () => {
        if (messages.length === 0) {
            setMessages((prevMessages) => [...prevMessages, { text: "There's no conversation history to generate insights from yet. Start by chatting!", sender: 'bot' }]);
            return;
        }

        setIsGeneratingInsights(true); // Show loading indicator for insights
        const insightsPrompt = "Based on the following conversation about carbon credits and green initiatives, please provide a concise summary or key insights. Focus on actionable takeaways or important concepts discussed:\n\n" +
            messages.map(msg => `${msg.sender === 'user' ? 'User' : 'Assistant'}: ${msg.text}`).join('\n');

        try {
            const payload = { contents: [{ role: "user", parts: [{ text: insightsPrompt }] }] };
            const apiKey = ""; // Leave this as is; Canvas will provide the API key at runtime
            const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`;

            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
            });

            const result = await response.json();

            if (result.candidates && result.candidates.length > 0 &&
                result.candidates[0].content && result.candidates[0].content.parts &&
                result.candidates[0].content.parts.length > 0) {
                const insightsText = result.candidates[0].content.parts[0].text;
                const insightsMessage = { text: `✨ Here are some insights based on our conversation:\n\n${insightsText}`, sender: 'bot' };
                setMessages((prevMessages) => [...prevMessages, insightsMessage]); // Add insights message
            } else {
                console.error("Unexpected API response structure for insights:", result);
                const errorMessage = { text: "Sorry, I couldn't generate insights. Please try again.", sender: 'bot' };
                setMessages((prevMessages) => [...prevMessages, errorMessage]);
            }
        } catch (error) {
            console.error("Error calling Gemini API for insights:", error);
            const errorMessage = { text: "There was an error generating insights. Please check your network or try again later.", sender: 'bot' };
            setMessages((prevMessages) => [...prevMessages, errorMessage]);
        } finally {
            setIsGeneratingInsights(false); // Hide loading indicator for insights
        }
    };

    return (
        <div className="flex-1 w-full max-w-2xl bg-gray-800 rounded-b-lg shadow-lg flex flex-col overflow-hidden">
            {/* Messages Display Area */}
            <div className="flex-1 p-4 overflow-y-auto custom-scrollbar">
                {messages.length === 0 && (
                    <div className="text-center text-gray-500 mt-10">
                        <p>Hello! I'm your EcoLedger AI Assistant.</p>
                        <p>Ask me anything about carbon credits, green initiatives, or provide your data for insights!</p>
                    </div>
                )}
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        className={`flex mb-4 ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                    >
                        <div
                            className={`max-w-[70%] p-3 rounded-lg shadow-md ${msg.sender === 'user'
                                    ? 'bg-green-600 text-white rounded-br-none'
                                    : 'bg-gray-700 text-gray-100 rounded-bl-none'
                                }`}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
                {(isLoading || isGeneratingInsights) && (
                    <div className="flex justify-start mb-4">
                        <div className="max-w-[70%] p-3 rounded-lg shadow-md bg-gray-700 text-gray-100 rounded-bl-none">
                            <div className="flex items-center">
                                <span className="animate-pulse mr-2">...</span> Thinking
                            </div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} /> {/* Empty div for scrolling */}
            </div>

            {/* Input Area */}
            <form onSubmit={sendMessage} className="p-4 border-t border-gray-700 flex items-center">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Type your message here..."
                    className="flex-1 p-3 rounded-lg bg-gray-700 text-gray-100 border border-gray-600 focus:outline-none focus:border-green-500 transition-all duration-200"
                    disabled={isLoading || isGeneratingInsights}
                />
                <button
                    type="submit"
                    className="ml-4 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={isLoading || isGeneratingInsights || input.trim() === ''}
                >
                    Send
                </button>
                <button
                    type="button" // Use type="button" to prevent form submission
                    onClick={generateInsights}
                    className="ml-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={isLoading || isGeneratingInsights || messages.length === 0}
                >
                    ✨ Get Insights
                </button>
            </form>
        </div>
    );
};

export default Chatbot;
